interface Props {
  userAgent: string;
}

interface Comp extends React.FC<Props> {
  getInitialProps: ({ req }: any) => Promise<Props>;
}

const Home: Comp = ({ userAgent }) => (
  <h1>Hello world! - user agent: {userAgent}</h1>
);

Home.getInitialProps = async ({ req }) => {
  const userAgent: string = req
    ? req.headers["user-agent"]
    : navigator.userAgent;
  return { userAgent };
};

export default Home;
